mapper.select:
select NVL(SUM(p_gprice * c_amount), 0) money
from cart c, product p
where c.p_code = p.p_code and c.c_id = #{c_id}

mapper.update:
update cart
set c_amount = #{c_amount}
where c_id = #{c_id}
and p_code = #{p_code}

mapper.select:
select count(*)
from cart
where c_id = #{userId}
and p_code = #{productId}

mapper.update:
update cart
set c_amount = c_amount + #{c_amount}
where c_id = #{c_id}
and p_code = #{p_code}
